import classes from './CartItem.module.css';
import {useDispatch} from 'react-redux';
import {cartActions} from "../../store/cart-slice"

const CartItem = (props) => {
  const { title, quantity, total, price } = props.item;

const dispach =useDispatch(cartActions);

const addItemHandler=()=>{
  dispatchEvent(cartActions.addItemToCart)
}

const removeItemHandler=()=>{
  dispatchEvent(cartActions.removeItemFromCart)
}
  return (
    <li className={classes.item}>
      <header>
        <h3>{title}</h3>
        <div className={classes.price}>
          ${total.toFixed(2)}{' '}
          <span className={classes.itemprice}>(${price.toFixed(2)}/item)</span>
        </div>
      </header>
      <div className={classes.details}>
        <div className={classes.quantity}>
          x <span>{quantity}</span>
        </div>
        <div className={classes.actions}>
          <button onClick={increaseButtonHandler}>-</button>
          <button onClic={removeItemHandler}>+</button>
        </div>
      </div>
    </li>
  );
};

export default CartItem;
