import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import Notification from '../src/components/UI/Notification'

import { useSelector } from 'react-redux';
import { useEffect } from 'react';
import { useDispatch } from 'react-redux';
import { uiActions } from './store/ui-slice'
import { Fragment } from 'react';

function App() {

  const dispatch = useDispatch;
  const showCart = useSelector(state => state.ui.cartIsVisible)
  const cart = useSelector((state) => state.cart)
  const notification = useSelector(state.ui.notification)
  useEffect(() => {

    const sendCartData = async () => {
      dispatch(uiActions.showNotification({
        status: 'pending',
        title: 'Sending',
        message: 'Sending  cart data!',
      })
      )
      const response = await fetch('https://react-http-a7c23-default-rtdb.europe-west1.firebasedatabase.app/cart.json',
        {
          method: 'PUT',
          body: JSON.stringify(cart)
        });

      if (!response.ok) {
        throw new Error('senfing cart data fail')
      }



      dispatch(uiActions.showNotification({
        status: 'success',
        title: 'Success',
        message: 'Sent car data succesfully!',
      })
      )

      sendCartData().catch(error => {

        dispatch(uiActions.showNotification({
          status: 'error',
          title: 'Error',
          message: 'Sending cart data failed!',
        })
        )

      });
    };
  }, [cart, dispatch]);

  return (

    <Fragment>
      <Notification status={notification.status} title={notification.title} message={notification.message}/>
      <Layout>
        {showCart && <Cart />}
        <Products />
      </Layout>
    </Fragment>

  );
}

export default App;
