}
      sendCartData().catch(error => {

        dispatch(uiActions.showNotification({
          status: 'error',
          title: 'Error',
          message: 'Sending cart data failed!',
        })
        )

      });
  }, [cart, dispatch]);

  return(
    <Fragment>
      {notification && <Notification 
      status={notification.status} 
      title={notification.title} 
      message={notification.message}/> }

      <Layout>
        {showCart && <Cart />}
        <Products />
      </Layout>
    </Fragment>

  );
}

export default App;
